@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&family=JetBrains+Mono:wght@400;500;600&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer utilities {
  /* AI/ML Background Animations */
  @keyframes slide-right {
    0% { transform: translateX(-100px); opacity: 0; }
    50% { opacity: 1; }
    100% { transform: translateX(100vw); opacity: 0; }
  }

  @keyframes slide-left {
    0% { transform: translateX(100px); opacity: 0; }
    50% { opacity: 1; }
    100% { transform: translateX(-100vw); opacity: 0; }
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  @keyframes float-delayed {
    0%, 100% { transform: translateY(-5px); }
    50% { transform: translateY(-15px); }
  }

  .animate-slide-right {
    animation: slide-right 15s infinite linear;
  }

  .animate-slide-left {
    animation: slide-left 18s infinite linear;
  }

  .animate-float {
    animation: float 3s ease-in-out infinite;
  }

  .animate-float-delayed {
    animation: float-delayed 3s ease-in-out infinite;
    animation-delay: 1.5s;
  }

  /* Matrix Digital Rain Effect */
  @keyframes matrix-fall {
    0% { transform: translateY(-20px); opacity: 0; }
    10% { opacity: 1; }
    90% { opacity: 1; }
    100% { transform: translateY(100vh); opacity: 0; }
  }

  @keyframes matrix-fall-delayed {
    0% { transform: translateY(-30px); opacity: 0; }
    15% { opacity: 1; }
    85% { opacity: 1; }
    100% { transform: translateY(100vh); opacity: 0; }
  }

  .animate-matrix-fall {
    animation: matrix-fall 8s infinite linear;
  }

  .animate-matrix-fall-delayed {
    animation: matrix-fall-delayed 10s infinite linear;
    animation-delay: 2s;
  }

  /* Particle System Animations */
  @keyframes float-particle {
    0%, 100% { transform: translateY(0px) scale(1); }
    25% { transform: translateY(-15px) scale(1.1); }
    50% { transform: translateY(-5px) scale(0.9); }
    75% { transform: translateY(-20px) scale(1.05); }
  }

  @keyframes float-particle-delayed {
    0%, 100% { transform: translateY(-10px) scale(1); }
    33% { transform: translateY(-25px) scale(1.2); }
    66% { transform: translateY(5px) scale(0.8); }
  }

  @keyframes rotate-particle {
    0% { transform: rotate(0deg) translateX(10px); }
    100% { transform: rotate(360deg) translateX(10px); }
  }

  @keyframes pulse-particle {
    0%, 100% { transform: scale(1); opacity: 0.4; }
    50% { transform: scale(1.5); opacity: 0.8; }
  }

  @keyframes particle-drift {
    0% { transform: translate(0, 0); }
    25% { transform: translate(20px, -10px); }
    50% { transform: translate(-15px, -20px); }
    75% { transform: translate(10px, -5px); }
    100% { transform: translate(0, 0); }
  }

  @keyframes particle-drift-delayed {
    0% { transform: translate(0, 0); }
    33% { transform: translate(-25px, 15px); }
    66% { transform: translate(20px, -25px); }
    100% { transform: translate(0, 0); }
  }

  @keyframes particle-orbit {
    0% { transform: rotate(0deg) translateX(15px) rotate(0deg); }
    100% { transform: rotate(360deg) translateX(15px) rotate(-360deg); }
  }

  @keyframes particle-spiral {
    0% { transform: rotate(0deg) translateX(5px) scale(1); }
    50% { transform: rotate(180deg) translateX(20px) scale(1.3); }
    100% { transform: rotate(360deg) translateX(5px) scale(1); }
  }

  @keyframes particle-bounce {
    0%, 100% { transform: translateY(0); }
    25% { transform: translateY(-20px); }
    50% { transform: translateY(-10px); }
    75% { transform: translateY(-25px); }
  }

  @keyframes particle-zigzag {
    0% { transform: translate(0, 0); }
    25% { transform: translate(15px, -10px); }
    50% { transform: translate(-10px, -20px); }
    75% { transform: translate(20px, -30px); }
    100% { transform: translate(0, -40px); }
  }

  /* Code Animation */
  @keyframes code-scroll {
    0% { transform: translateY(10px); opacity: 0; }
    50% { opacity: 1; }
    100% { transform: translateY(-10px); opacity: 0; }
  }

  @keyframes code-scroll-delayed {
    0% { transform: translateY(15px); opacity: 0; }
    60% { opacity: 1; }
    100% { transform: translateY(-15px); opacity: 0; }
  }

  /* Grid Pattern Animation */
  @keyframes grid-pulse {
    0%, 100% { border-opacity: 0.1; }
    50% { border-opacity: 0.3; }
  }

  /* Enhanced Pulse Animation */
  @keyframes pulse-slow {
    0%, 100% { opacity: 0.3; }
    50% { opacity: 0.7; }
  }

  /* Applying All Animations */
  .animate-float-particle {
    animation: float-particle 4s ease-in-out infinite;
  }

  .animate-float-particle-delayed {
    animation: float-particle-delayed 5s ease-in-out infinite;
    animation-delay: 2s;
  }

  .animate-rotate-particle {
    animation: rotate-particle 6s linear infinite;
  }

  .animate-pulse-particle {
    animation: pulse-particle 3s ease-in-out infinite;
  }

  .animate-particle-drift {
    animation: particle-drift 8s ease-in-out infinite;
  }

  .animate-particle-drift-delayed {
    animation: particle-drift-delayed 10s ease-in-out infinite;
    animation-delay: 3s;
  }

  .animate-particle-orbit {
    animation: particle-orbit 12s linear infinite;
  }

  .animate-particle-spiral {
    animation: particle-spiral 7s ease-in-out infinite;
  }

  .animate-particle-bounce {
    animation: particle-bounce 4s ease-in-out infinite;
  }

  .animate-particle-zigzag {
    animation: particle-zigzag 6s linear infinite;
  }

  .animate-code-scroll {
    animation: code-scroll 8s ease-in-out infinite;
  }

  .animate-code-scroll-delayed {
    animation: code-scroll-delayed 9s ease-in-out infinite;
    animation-delay: 4s;
  }

  .animate-grid-pulse {
    animation: grid-pulse 4s ease-in-out infinite;
  }

  .animate-pulse-slow {
    animation: pulse-slow 6s ease-in-out infinite;
  }

  /* Agentic AI Animations */
  @keyframes agent-thinking {
    0%, 100% { transform: scale(1) rotate(0deg); }
    25% { transform: scale(1.05) rotate(2deg); }
    50% { transform: scale(0.95) rotate(-2deg); }
    75% { transform: scale(1.02) rotate(1deg); }
  }

  @keyframes pathfinding {
    0%, 100% { opacity: 0.25; }
    50% { opacity: 0.4; }
  }

  @keyframes agent-start {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.3); }
  }

  @keyframes agent-move {
    0% { opacity: 1; }
    100% { opacity: 0.8; }
  }

  /* Swarm Intelligence Animations */
  @keyframes swarm-agent-1 {
    0% { transform: translate(0, 0); }
    25% { transform: translate(10px, -5px); }
    50% { transform: translate(-5px, 10px); }
    75% { transform: translate(8px, 3px); }
    100% { transform: translate(0, 0); }
  }

  @keyframes swarm-agent-2 {
    0% { transform: translate(0, 0); }
    25% { transform: translate(-8px, 6px); }
    50% { transform: translate(4px, -8px); }
    75% { transform: translate(-6px, -4px); }
    100% { transform: translate(0, 0); }
  }

  @keyframes swarm-agent-3 {
    0% { transform: translate(0, 0); }
    25% { transform: translate(6px, 8px); }
    50% { transform: translate(-10px, -6px); }
    75% { transform: translate(4px, 7px); }
    100% { transform: translate(0, 0); }
  }

  @keyframes swarm-agent-4 {
    0% { transform: translate(0, 0); }
    25% { transform: translate(-4px, -9px); }
    50% { transform: translate(7px, 5px); }
    75% { transform: translate(-8px, 8px); }
    100% { transform: translate(0, 0); }
  }

  @keyframes swarm-center {
    0%, 100% { transform: translate(-50%, -50%) scale(1); }
    50% { transform: translate(-50%, -50%) scale(1.2); }
  }

  /* Reasoning Chain Animations */
  @keyframes reasoning-chain {
    0%, 100% { opacity: 0.25; }
    50% { opacity: 0.4; }
  }

  @keyframes reason-step-1 {
    0% { background-color: rgba(147,197,253,0.6); transform: scale(1); }
    33% { background-color: rgba(34,197,94,0.8); transform: scale(1.1); }
    66% { background-color: rgba(147,197,253,0.6); transform: scale(1); }
    100% { background-color: rgba(147,197,253,0.6); transform: scale(1); }
  }

  @keyframes reason-step-2 {
    0% { background-color: rgba(168,85,247,0.6); transform: scale(1); }
    33% { background-color: rgba(168,85,247,0.6); transform: scale(1); }
    66% { background-color: rgba(34,197,94,0.8); transform: scale(1.1); }
    100% { background-color: rgba(168,85,247,0.6); transform: scale(1); }
  }

  @keyframes reason-step-3 {
    0% { background-color: rgba(34,197,94,0.6); transform: scale(1); }
    66% { background-color: rgba(34,197,94,0.6); transform: scale(1); }
    100% { background-color: rgba(34,197,94,0.8); transform: scale(1.1); }
  }

  @keyframes reason-arrow-1 {
    0%, 33% { opacity: 0.3; }
    66%, 100% { opacity: 0.8; }
  }

  @keyframes reason-arrow-2 {
    0%, 66% { opacity: 0.3; }
    100% { opacity: 0.8; }
  }

  /* Planning Algorithm Animation */
  @keyframes planning-algo {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.05); }
  }

  /* Agent Network Animation */
  @keyframes agent-network {
    0%, 100% { opacity: 0.25; }
    50% { opacity: 0.4; }
  }

  /* Reward Signal Animation */
  @keyframes reward-signal {
    0%, 100% { transform: scale(1); background-color: rgba(34,197,94,0.8); }
    50% { transform: scale(1.3); background-color: rgba(34,197,94,1); }
  }

  /* Text Generation Animation */
  @keyframes text-generation {
    0%, 100% { opacity: 0.2; }
    50% { opacity: 0.4; }
  }

  @keyframes typing-1 {
    0% { opacity: 0; width: 0; }
    25% { opacity: 1; width: 100%; }
    75% { opacity: 1; width: 100%; }
    100% { opacity: 0; width: 0; }
  }

  @keyframes typing-2 {
    0%, 25% { opacity: 0; width: 0; }
    50% { opacity: 1; width: 100%; }
    75% { opacity: 1; width: 100%; }
    100% { opacity: 0; width: 0; }
  }

  @keyframes typing-3 {
    0%, 50% { opacity: 0; width: 0; }
    75% { opacity: 1; width: 100%; }
    100% { opacity: 1; width: 100%; }
  }

  /* Apply Agentic AI Animations */
  .animate-agent-thinking {
    animation: agent-thinking 4s ease-in-out infinite;
  }

  .animate-pathfinding {
    animation: pathfinding 6s ease-in-out infinite;
  }

  .animate-agent-start {
    animation: agent-start 2s ease-in-out infinite;
  }

  .animate-agent-move {
    animation: agent-move 8s linear infinite;
  }

  .animate-swarm-agent-1 {
    animation: swarm-agent-1 5s ease-in-out infinite;
  }

  .animate-swarm-agent-2 {
    animation: swarm-agent-2 6s ease-in-out infinite;
  }

  .animate-swarm-agent-3 {
    animation: swarm-agent-3 4.5s ease-in-out infinite;
  }

  .animate-swarm-agent-4 {
    animation: swarm-agent-4 5.5s ease-in-out infinite;
  }

  .animate-swarm-center {
    animation: swarm-center 3s ease-in-out infinite;
  }

  .animate-reasoning-chain {
    animation: reasoning-chain 8s ease-in-out infinite;
  }

  .animate-reason-step-1 {
    animation: reason-step-1 6s ease-in-out infinite;
  }

  .animate-reason-step-2 {
    animation: reason-step-2 6s ease-in-out infinite;
  }

  .animate-reason-step-3 {
    animation: reason-step-3 6s ease-in-out infinite;
  }

  .animate-reason-arrow-1 {
    animation: reason-arrow-1 6s ease-in-out infinite;
  }

  .animate-reason-arrow-2 {
    animation: reason-arrow-2 6s ease-in-out infinite;
  }

  .animate-planning-algo {
    animation: planning-algo 4s ease-in-out infinite;
  }

  .animate-agent-network {
    animation: agent-network 5s ease-in-out infinite;
  }

  .animate-reward-signal {
    animation: reward-signal 2s ease-in-out infinite;
  }

  .animate-text-generation {
    animation: text-generation 8s ease-in-out infinite;
  }

  .animate-typing-1 {
    animation: typing-1 6s ease-in-out infinite;
    overflow: hidden;
    white-space: nowrap;
  }

  .animate-typing-2 {
    animation: typing-2 6s ease-in-out infinite;
    overflow: hidden;
    white-space: nowrap;
  }

  .animate-typing-3 {
    animation: typing-3 6s ease-in-out infinite;
    overflow: hidden;
    white-space: nowrap;
  }

  /* Enhanced Website Animations */
  @keyframes section-slide-up {
    0% { 
      opacity: 0; 
      transform: translateY(50px); 
    }
    100% { 
      opacity: 1; 
      transform: translateY(0); 
    }
  }

  @keyframes fade-in-up {
    0% { 
      opacity: 0; 
      transform: translateY(30px); 
    }
    100% { 
      opacity: 1; 
      transform: translateY(0); 
    }
  }

  @keyframes text-glow {
    0%, 100% { 
      text-shadow: 0 0 5px rgba(59,130,246,0.3); 
    }
    50% { 
      text-shadow: 0 0 20px rgba(59,130,246,0.6), 0 0 30px rgba(59,130,246,0.4); 
    }
  }

  @keyframes text-fade-in {
    0% { 
      opacity: 0; 
      transform: translateY(20px); 
    }
    100% { 
      opacity: 1; 
      transform: translateY(0); 
    }
  }

  @keyframes card-float {
    0%, 100% { 
      transform: translateY(0); 
    }
    50% { 
      transform: translateY(-5px); 
    }
  }

  @keyframes card-stagger {
    0% { 
      opacity: 0; 
      transform: translateY(50px) scale(0.8); 
    }
    100% { 
      opacity: 1; 
      transform: translateY(0) scale(1); 
    }
  }

  @keyframes button-pulse {
    0%, 100% { 
      box-shadow: 0 0 0 0 rgba(59,130,246,0.4); 
    }
    50% { 
      box-shadow: 0 0 0 10px rgba(59,130,246,0); 
    }
  }

  @keyframes bounce-subtle {
    0%, 100% { 
      transform: translateY(0); 
    }
    50% { 
      transform: translateY(-3px); 
    }
  }

  @keyframes nav-slide-down {
    0% { 
      opacity: 0; 
      transform: translateY(-100%); 
    }
    100% { 
      opacity: 1; 
      transform: translateY(0); 
    }
  }

  @keyframes hero-title-reveal {
    0% { 
      opacity: 0; 
      transform: translateY(50px) scale(0.8); 
    }
    100% { 
      opacity: 1; 
      transform: translateY(0) scale(1); 
    }
  }

  @keyframes hero-subtitle-reveal {
    0% { 
      opacity: 0; 
      transform: translateX(-50px); 
    }
    100% { 
      opacity: 1; 
      transform: translateX(0); 
    }
  }

  @keyframes shimmer {
    0% { 
      background-position: -1000px 0; 
    }
    100% { 
      background-position: 1000px 0; 
    }
  }

  @keyframes icon-spin {
    0% { 
      transform: rotate(0deg); 
    }
    100% { 
      transform: rotate(360deg); 
    }
  }

  @keyframes scale-up {
    0% { 
      transform: scale(1); 
    }
    100% { 
      transform: scale(1.05); 
    }
  }

  @keyframes gradient-shift {
    0%, 100% { 
      background-position: 0% 50%; 
    }
    50% { 
      background-position: 100% 50%; 
    }
  }

  /* Apply Enhanced Website Animations */
  .animate-section-slide-up {
    animation: section-slide-up 0.8s ease-out forwards;
  }

  .animate-fade-in-up {
    animation: fade-in-up 0.6s ease-out forwards;
  }

  .animate-text-glow {
    animation: text-glow 3s ease-in-out infinite;
  }

  .animate-text-fade-in {
    animation: text-fade-in 0.8s ease-out 0.3s forwards;
    opacity: 0;
  }

  .animate-card-float {
    animation: card-float 4s ease-in-out infinite;
  }

  .animate-card-stagger {
    animation: card-stagger 0.6s ease-out forwards;
    opacity: 0;
  }

  .animate-button-pulse {
    animation: button-pulse 2s ease-in-out infinite;
  }

  .animate-bounce-subtle {
    animation: bounce-subtle 2s ease-in-out infinite;
  }

  .animate-nav-slide-down {
    animation: nav-slide-down 0.5s ease-out;
  }

  .animate-hero-title-reveal {
    animation: hero-title-reveal 1s ease-out;
  }

  .animate-hero-subtitle-reveal {
    animation: hero-subtitle-reveal 1s ease-out 0.3s forwards;
    opacity: 0;
  }

  .animate-shimmer {
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.4), transparent);
    background-size: 200% 100%;
    animation: shimmer 3s ease-in-out infinite;
  }

  .animate-icon-spin:hover {
    animation: icon-spin 0.5s ease-in-out;
  }

  .animate-scale-up:hover {
    animation: scale-up 0.3s ease-out forwards;
  }

  .animate-gradient-shift {
    background: linear-gradient(-45deg, #ee7752, #e73c7e, #23a6d5, #23d5ab);
    background-size: 400% 400%;
    animation: gradient-shift 4s ease infinite;
  }

  /* Intersection Observer Animation Classes */
  .fade-in-on-scroll {
    opacity: 0;
    transform: translateY(50px);
    transition: all 0.6s ease-out;
  }

  .fade-in-on-scroll.visible {
    opacity: 1;
    transform: translateY(0);
  }

  .slide-in-left {
    opacity: 0;
    transform: translateX(-100px);
    transition: all 0.8s ease-out;
  }

  .slide-in-left.visible {
    opacity: 1;
    transform: translateX(0);
  }

  .slide-in-right {
    opacity: 0;
    transform: translateX(100px);
    transition: all 0.8s ease-out;
  }

  .slide-in-right.visible {
    opacity: 1;
    transform: translateX(0);
  }

  .scale-in {
    opacity: 0;
    transform: scale(0.8);
    transition: all 0.6s ease-out;
  }

  .scale-in.visible {
    opacity: 1;
    transform: scale(1);
  }

  /* Interactive Hover Effects */
  .hover-lift {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .hover-lift:hover {
    transform: translateY(-5px);
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  }

  .hover-glow {
    transition: all 0.3s ease;
  }

  .hover-glow:hover {
    box-shadow: 0 0 20px rgba(59,130,246,0.4);
    transform: scale(1.02);
  }

  .hover-rotate {
    transition: transform 0.3s ease;
  }

  .hover-rotate:hover {
    transform: rotate(5deg);
  }

  /* Staggered Animation Utilities */
  .stagger-delay-1 { animation-delay: 0.1s; }
  .stagger-delay-2 { animation-delay: 0.2s; }
  .stagger-delay-3 { animation-delay: 0.3s; }
  .stagger-delay-4 { animation-delay: 0.4s; }
  .stagger-delay-5 { animation-delay: 0.5s; }
  .stagger-delay-6 { animation-delay: 0.6s; }
}

