@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/* Custom gradient utilities */
.gradient-blue {
  background: linear-gradient(135deg, var(--aaai-blue), rgb(59, 130, 246));
}

.gradient-green {
  background: linear-gradient(135deg, rgb(34, 197, 94), rgb(22, 163, 74));
}

.gradient-purple {
  background: linear-gradient(135deg, var(--aaai-purple), rgb(147, 51, 234));
}

.gradient-pink {
  background: linear-gradient(135deg, var(--aaai-pink), rgb(236, 72, 153));
}

.gradient-indigo {
  background: linear-gradient(135deg, var(--aaai-indigo), rgb(99, 102, 241));
}

.gradient-teal {
  background: linear-gradient(135deg, var(--aaai-teal), rgb(20, 184, 166));
}

.gradient-orange {
  background: linear-gradient(135deg, var(--aaai-orange), rgb(249, 115, 22));
}

.gradient-hero {
  background: linear-gradient(135deg, var(--aaai-blue) 0%, var(--aaai-purple) 35%, var(--aaai-cyan) 100%);
}

/* Code highlighting styles */
.code-block {
  @apply bg-gray-900 text-gray-300 font-mono text-sm rounded-lg p-4 overflow-x-auto;
}

.code-keyword {
  @apply text-pink-400;
}

.code-string {
  @apply text-green-400;
}

.code-number {
  @apply text-blue-400;
}

.code-comment {
  @apply text-gray-500;
}

.code-function {
  @apply text-yellow-400;
}

/* Logo Animation Keyframes */
@keyframes float-1 {
  0%, 100% { transform: translateY(0px) translateX(0px); opacity: 0.7; }
  25% { transform: translateY(-3px) translateX(1px); opacity: 1; }
  50% { transform: translateY(-1px) translateX(-1px); opacity: 0.8; }
  75% { transform: translateY(-2px) translateX(1px); opacity: 0.9; }
}

@keyframes float-2 {
  0%, 100% { transform: translateY(0px) translateX(0px); opacity: 0.6; }
  33% { transform: translateY(-2px) translateX(-1px); opacity: 1; }
  66% { transform: translateY(-4px) translateX(1px); opacity: 0.8; }
}

@keyframes float-3 {
  0%, 100% { transform: translateY(0px) translateX(0px); opacity: 0.8; }
  50% { transform: translateY(-2px) translateX(-2px); opacity: 1; }
}

@keyframes spin-slow {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

/* Logo Animation Classes */
.animate-float-1 {
  animation: float-1 3s ease-in-out infinite;
}

.animate-float-2 {
  animation: float-2 4s ease-in-out infinite;
}

.animate-float-3 {
  animation: float-3 5s ease-in-out infinite;
}

.animate-spin-slow {
  animation: spin-slow 8s linear infinite;
}

