import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Clock, Users, BookOpen, ChevronDown } from "lucide-react";
import { Course } from "@shared/schema";
import { useState } from "react";

interface CoachingCourseCardProps {
  course: Course;
}

export default function CoachingCourseCard({ course }: CoachingCourseCardProps) {
  const [showModules, setShowModules] = useState(false);
  const modules = course.modules || [];

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(price);
  };

  const getBadgeColor = (difficulty: string) => {
    switch (difficulty.toLowerCase()) {
      case 'beginner level':
        return 'bg-green-100 text-green-800 border-green-200';
      case 'intermediate level':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'advanced level':
        return 'bg-red-100 text-red-800 border-red-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  return (
    <Card className="relative overflow-hidden border-2 border-gray-100 hover:border-gray-300 transition-all duration-300 hover:shadow-xl">
      {/* Header with gradient background */}
      <CardHeader className={`bg-gradient-to-r ${course.color} text-white p-6 relative`}>
        <div className="absolute top-4 right-4">
          <Badge className="bg-white/20 text-white text-xs px-2 py-1">
            {course.category}
          </Badge>
        </div>

        <div className="space-y-2">
          <h3 className="text-2xl font-bold">{course.title}</h3>
          <p className="text-white/90 text-sm">{course.description}</p>
        </div>
      </CardHeader>

      <CardContent className="p-6">
        {/* Course Stats */}
        <div className="flex items-center justify-between mb-4 text-sm text-gray-600">
          <div className="flex items-center gap-4">
            <div className="flex items-center">
              <Clock className="h-4 w-4 mr-1" />
              <span>{modules.length} Modules</span>
            </div>
            <div className="flex items-center">
              <BookOpen className="h-4 w-4 mr-1" />
              <span>{course.weeks}</span>
            </div>
          </div>
          <Badge className={getBadgeColor(course.difficulty)}>
            {course.difficulty}
          </Badge>
        </div>

        {/* Modules Dropdown */}
        <div className="mb-6">
          <Button
            variant="ghost"
            onClick={() => setShowModules(!showModules)}
            className="w-full justify-between p-3 bg-gray-50 hover:bg-gray-100 rounded-lg"
          >
            <span className="font-medium">View Modules</span>
            <ChevronDown className={`h-4 w-4 transition-transform ${showModules ? 'rotate-180' : ''}`} />
          </Button>

          {showModules && (
            <div className="mt-3 space-y-2 bg-gray-50 p-4 rounded-lg">
              {modules.map((module, index) => (
                <div key={module.id || index} className="flex items-center text-sm">
                  <span className="w-6 h-6 bg-blue-100 text-blue-600 rounded-full flex items-center justify-center text-xs font-medium mr-3">
                    {index + 1}
                  </span>
                  <span>{module.title}</span>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Price */}
        <div className="text-center mb-6">
          <div className="text-3xl font-bold text-gray-900">
            {course.price ? formatPrice(course.price) : 'Contact Us'}
          </div>
        </div>

        {/* CTA Button */}
        <Button className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3 rounded-lg font-semibold">
          Learn More
        </Button>
      </CardContent>
    </Card>
  );
}

client/src/components/code-lab.tsx.content:
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Play, RotateCcw, Save, Lightbulb, Clock, CheckCircle } from "lucide-react";
import { CodeLab } from "@shared/schema";
import { DIFFICULTY_COLORS } from "@/lib/constants";
import { useState } from "react";

interface CodeLabProps {
  lab: CodeLab;
}

export default function CodeLabComponent({ lab }: CodeLabProps) {
  const [currentCode, setCurrentCode] = useState(lab.starterCode);
  const [output, setOutput] = useState(">>> Waiting for code execution...\nHint: Complete the implementation to see results");
  const [isRunning, setIsRunning] = useState(false);

  const handleRun = async () => {
    setIsRunning(true);
    // Simulate code execution
    setTimeout(() => {
      setOutput(`>>> Running ${lab.title}...\nModel initialized successfully\nTraining data shape: (100, 1)\nTarget data shape: (100, 1)\n\n${currentCode.includes('training loop') ? 'Training completed successfully!' : 'Waiting for implementation...'}`);
      setIsRunning(false);
    }, 1500);
  };

  const handleReset = () => {
    setCurrentCode(lab.starterCode);
    setOutput(">>> Code reset to starter template\nReady for implementation");
  };

  const handleSave = () => {
    localStorage.setItem(`lab_${lab.id}`, currentCode);
    setOutput(">>> Code saved successfully\nYour progress has been saved");
  };

  return (
    <Card className="overflow-hidden shadow-2xl bg-gray-900 text-white">
      {/* Lab Header */}
      <CardHeader className="bg-gray-800 border-b border-gray-700">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <h3 className="text-lg font-semibold text-white">{lab.title}</h3>
            <Badge className={DIFFICULTY_COLORS[lab.difficulty as keyof typeof DIFFICULTY_COLORS]}>
              {lab.difficulty}
            </Badge>
          </div>
          <div className="flex items-center space-x-3">
            <Button
              variant="ghost"
              size="sm"
              className="text-gray-400 hover:text-white"
              onClick={handleReset}
            >
              <RotateCcw className="h-4 w-4 mr-2" />
              Reset
            </Button>
            <Button
              variant="ghost"
              size="sm"
              className="text-gray-400 hover:text-white"
              onClick={handleSave}
            >
              <Save className="h-4 w-4 mr-2" />
              Save
            </Button>
            <Button
              className="bg-green-600 hover:bg-green-700"
              onClick={handleRun}
              disabled={isRunning}
            >
              <Play className="h-4 w-4 mr-2" />
              {isRunning ? "Running..." : "Run"}
            </Button>
          </div>
        </div>
      </CardHeader>

      {/* Code Editor Area */}
      <div className="grid grid-cols-1 lg:grid-cols-2 h-96">
        {/* Code Editor */}
        <div className="bg-gray-900 p-6 overflow-auto">
          <textarea
            value={currentCode}
            onChange={(e) => setCurrentCode(e.target.value)}
            className="w-full h-full bg-transparent text-gray-300 font-mono text-sm leading-relaxed resize-none outline-none"
            spellCheck={false}
          />
        </div>

        {/* Output Console */}
        <div className="bg-gray-800 border-l border-gray-700">
          <div className="bg-gray-700 px-4 py-2 border-b border-gray-600">
            <h4 className="text-white font-medium">Output Console</h4>
          </div>
          <div className="p-4 h-full overflow-auto">
            <pre className="text-green-400 font-mono text-sm whitespace-pre-wrap">
              {output}
            </pre>
          </div>
        </div>
      </div>

      {/* Lab Instructions */}
      <CardContent className="bg-gray-800 border-t border-gray-700">
        <h4 className="text-white font-semibold mb-3">Instructions:</h4>
        <div className="text-gray-300 mb-4">
          <div className="prose prose-invert max-w-none text-sm">
            <div dangerouslySetInnerHTML={{ __html: lab.instructions.replace(/\n/g, '<br/>') }} />
          </div>
        </div>

        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="flex items-center text-gray-400 text-sm">
              <CheckCircle className="h-4 w-4 mr-1" />
              <span>Progress: 2/4 steps completed</span>
            </div>
            <div className="flex items-center text-gray-400 text-sm">
              <Clock className="h-4 w-4 mr-1" />
              <span>Time: {lab.estimatedTime}min</span>
            </div>
          </div>
          <Button 
            variant="ghost" 
            size="sm"
            className="text-blue-400 hover:text-blue-300"
            onClick={() => {
              if (lab.hints && lab.hints.length > 0) {
                alert(`Hint: ${lab.hints[0]}`);
              }
            }}
          >
            <Lightbulb className="h-4 w-4 mr-2" />
            Get Hint
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/course-card.tsx.content:
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Clock, CheckCircle, Circle } from "lucide-react";
import { Course } from "@shared/schema";
import { DIFFICULTY_COLORS } from "@/lib/constants";
import { Link } from "wouter";

interface CourseCardProps {
  course: Course;
  progress?: number;
}

export default function CourseCard({ course, progress = 0 }: CourseCardProps) {
  const modules = course.modules || [];
  const completedModules = modules.filter((m) => m.completed).length;
  const totalModules = modules.length;
  const isStarted = progress > 0;
  const isCompleted = progress >= 100;

  return (
    <Card className="overflow-hidden border border-gray-200 dark:border-gray-700 hover:shadow-xl transition-shadow duration-300">
      <CardHeader className={`bg-gradient-to-r ${course.color} p-6 text-white`}>
        <div className="flex items-center justify-between mb-4">
          <i className={`${course.icon} text-2xl`}></i>
          <Badge className="bg-white/20 text-white hover:bg-white/30">
            Course {course.id === 'foundation' ? '1' : course.id === 'machine-learning' ? '2' : course.id === 'deep-learning' ? '3' : course.id === 'generative-ai' ? '4' : course.id === 'llm' ? '5' : course.id === 'multimodal-ai' ? '6' : '7'}
          </Badge>
        </div>
        <h3 className="text-xl font-bold mb-2">{course.title}</h3>
        <p className="text-white/90">{course.description}</p>
      </CardHeader>

      <CardContent className="p-6">
        <div className="space-y-3 mb-6">
          {modules.slice(0, 4).map((module, index) => (
            <div key={module.id || index} className="flex items-center text-sm text-gray-600 dark:text-gray-400">
              {module.completed ? (
                <CheckCircle className="h-4 w-4 text-green-500 mr-2" />
              ) : (
                <Circle className="h-4 w-4 text-gray-300 mr-2" />
              )}
              <span>{module.title}</span>
            </div>
          ))}
        </div>

        {/* Progress Bar */}
        <div className="mb-4">
          <div className="flex justify-between text-sm text-gray-600 dark:text-gray-400 mb-2">
            <span>Progress</span>
            <span>{Math.round((completedModules / totalModules) * 100)}%</span>
          </div>
          <Progress value={(completedModules / totalModules) * 100} className="h-2" />
        </div>

        {/* Course Info */}
        <div className="flex items-center justify-between mb-4 text-sm text-gray-500 dark:text-gray-400">
          <div className="flex items-center">
            <Clock className="h-4 w-4 mr-1" />
            <span>{course.estimatedHours}h</span>
          </div>
          <Badge className={DIFFICULTY_COLORS[course.difficulty as keyof typeof DIFFICULTY_COLORS]}>
            {course.difficulty}
          </Badge>
        </div>

        <Link href={`/course/${course.id}`}>
          <Button 
            className="w-full"
            variant={isCompleted ? "outline" : "default"}
          >
            {isCompleted ? "Review Course" : isStarted ? "Continue Learning" : "Start Course"}
          </Button>
        </Link>
      </CardContent>
    </Card>
  );
}

