          ) : (
            <>
              <div className="mb-6">
                <p className="text-gray-600 dark:text-gray-400">
                  Showing {filteredDocs.length} document{filteredDocs.length !== 1 ? 's' : ''}
                  {selectedCategory !== "all" && ` in ${selectedCategory.replace(/-/g, ' ')}`}
                  {searchQuery && ` matching "${searchQuery}"`}
